INSERT INTO `hx_active` VALUES ('15', '42', '夫人联谊', '', '企业家夫人联谊会是针对女企业家与企业家夫人专门设立的活动组织，为了促进女企业家和企业家夫人的团结友谊，丰富女企业家和企业家夫人的业余生活，更好地学习、交流和提升，弘扬积极健康向上的生活理念，展现女性巾帼风采，推动彼此的共同事业，令成功企业家俱乐部的夫人联谊会成为娘家，让成功企业家俱乐部的成员们充分感受大家庭的温暖。', '本网讯', '封叶', '', 'http://hongxinguoji.com/group1/M00/00/07/Zckdh1cUiqOAMcu3AACXOYF1N8w840.jpg', '1', null, '2016-04-20 10:56:10', null, '2016-4-20');
