INSERT INTO `hx_active` VALUES ('17', '123', '国内外礼仪', '', '随着时代的演变，女性的角色亦产生了革命性的转变。在抛开传统的束缚后，取而代之的是时尚的穿着和自信的气质。光鲜亮丽的背后，是对自我实现与事业理想的追求。 不可避免的，企业家夫人的定位也大异以往。本课程通过在礼仪、形象、照顾家庭与协助企业经营管理等方面的教导，将告知身为企业家夫人的您，如何提升自身的气质与能力，成为成功男人背后的推手，进一步成为卓越男人的得力助手！', '本网讯', '心雨', '', '', '1', null, '2016-04-19 17:49:11', null, '2016-4-13');
